import "./styles.css";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import datajson from "./data";
import { Row, CardBody } from "reactstrap";

export default function App() {
  const [data, setData] = useState([]);
  const [tableda, setTableDa] = useState([]);

  useEffect(() => {
    const columns = [
      {
        id: 1,
        name: "Title",
        sortable: true,
        reorder: true,
        sorting_name: "title",
        wrap: true
      },
      {
        id: 2,
        name: "Poster",
        sortable: true,
        reorder: true,
        sorting_name: "poster",
        wrap: true
      },
      {
        id: 3,
        name: "Genre",
        sortable: true,
        reorder: true,
        sorting_name: "genre",
        wrap: true
      },
      {
        id: 4,
        name: "Rating",
        sortable: true,
        reorder: true,
        sorting_name: "Ratings",
        wrap: true
      },
      {
        id: 5,
        name: "Year Release",
        sortable: true,
        reorder: true,
        sorting_name: "year",
        wrap: true
      },
      {
        id: 6,
        name: "Metacritic Rating",
        sortable: true,
        reorder: true,
        sorting_name: "rated",
        wrap: true
      },
      {
        id: 7,
        name: "Runtime",
        sortable: true,
        reorder: true,
        sorting_name: "runtime",
        wrap: true
      }
    ];

    setData(columns);

    const res = [
      {
        title: "In the Mood for Love",
        year: "2000",
        rated: "PG",
        released: "09 Mar 2001",
        runtime: "98 min",
        genre: "drama",
        poster: "sdfgh",
        Ratings: "dfgh"
      },
      {
        title: "In the Mood for Love",
        year: "2000",
        rated: "PG",
        released: "09 Mar 2001",
        runtime: "98 min",
        poster: "dffgf",
        genre: "Romance",
        Ratings: "dfg"
      }
    ];

    setTableDa(res);

  }, []);

  return (
    <div className="App">
      <h1>Movies List</h1>
      <CardBody>
        <Row>
          <DataTable columns={data} data={tableda} defaultSortFieldId={1} />
        </Row>
      </CardBody>
    </div>
  );
}
